import "./App.scss"
import { useState, useEffect } from "react"
import Login from "./Pages/Login"
import GraphQLFetch from "./Helpers/GraphQLFetch"

function App() {
  const [auth, setAuth] = useState({ error: "", data: {}, loading: false })

  useEffect(() => {
    loadAuth()
  }, [])

  async function loadAuth() {
    setAuth((a) => (a.loading = true))
    let { errors, data } = await GraphQLFetch(`loggedIn{
      name
      email
      type
    }`)
    if (errors) {
      setAuth({ error: errors[0].message, data: {}, loading: false })
    } else {
      setAuth({ error: "", data, loading: false })
    }
  }

  return auth.loading ? (
    <>Loading...</>
  ) : (
    <>{auth.error ? <Login reloadAuth={() => loadAuth()} /> : <>Welcome</>}</>
  )
}

export default App
