import React, { useState } from "react"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {
  faAt,
  faLock,
  faAngleRight,
  faExclamationCircle,
  faGraduationCap,
  faUserAlt,
} from "@fortawesome/free-solid-svg-icons"
import { motion } from "framer-motion"
import { useLazyQuery, gql } from "@apollo/client"

function Login() {
  const [loginAs, setLoginAs] = useState(0) // 0 student 1 admin
  const [adminEmail, setAdminEmail] = useState("")
  const [adminPassword, setAdminPassword] = useState("")
  const [stdEmail, setStdEmail] = useState("")
  const [stdPassword, setStdPassword] = useState("")

  const std_query = gql`{
      studentLogin(reg_no: "${stdEmail}", password: "${stdPassword}"){
          token
      }
  }`

  const [loginStudent, stdResp] = useLazyQuery(std_query)

  return (
    <div className="login">
      {loginAs ? (
        <motion.form
          key="admin"
          layout
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          //   transition={{ duration: 2 }}
          onSubmit={e => {
            e.preventDefault()
          }}
        >
          <div className="title">Admin Login</div>
          <div className="message">
            <FontAwesomeIcon icon={faExclamationCircle} />
            <span>Server Message</span>
          </div>
          <div className="input">
            <label htmlFor="email">
              <FontAwesomeIcon icon={faAt} />
            </label>
            <input
              onChange={e => setAdminEmail(e.target.value)}
              value={adminEmail}
              type="email"
              placeholder="Email Address"
              name=""
              id=""
              required
            />
          </div>
          <div className="input">
            <label htmlFor="email">
              <FontAwesomeIcon icon={faLock} />
            </label>
            <input
              onChange={e => setAdminPassword(e.target.value)}
              value={adminPassword}
              type="password"
              placeholder="Password"
              name=""
              id=""
              required
            />
          </div>
          <button className="def-btn" key="adminlogin">
            <span>Log In</span>
            <FontAwesomeIcon icon={faAngleRight} />
          </button>
        </motion.form>
      ) : (
        <motion.form
          key="student"
          layout
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          //   transition={{ duration: 1 }}
          onSubmit={e => {
            e.preventDefault()
            loginStudent()
          }}
        >
          <div className="title">Student Login</div>
          <div className="message">
            <FontAwesomeIcon icon={faExclamationCircle} />
            <span>Server Message</span>
          </div>
          <div className="input">
            <label htmlFor="email">
              <FontAwesomeIcon icon={faGraduationCap} />
            </label>
            <input
              onChange={e => setStdEmail(e.target.value)}
              value={stdEmail}
              type="text"
              placeholder="Registration Number"
              name=""
              id=""
              required
            />
          </div>
          <div className="input">
            <label htmlFor="email">
              <FontAwesomeIcon icon={faLock} />
            </label>
            <input
              onChange={e => setStdPassword(e.target.value)}
              value={stdPassword}
              type="password"
              placeholder="Password"
              name=""
              id=""
              required
            />
          </div>
          <button className="def-btn" key="stdlogin">
            <span>Log In</span>
            <FontAwesomeIcon icon={faAngleRight} />
          </button>
        </motion.form>
      )}
      <div className="toggle-login-as">
        <div
          className="option sel"
          onClick={e => {
            let bts = document.querySelectorAll(".toggle-login-as .option")
            for (let b of bts) b.classList.remove("sel")
            e.target.classList.add("sel")
            setAdminEmail("")
            setAdminPassword("")
            setStdEmail("")
            setStdPassword("")
            setLoginAs(0)
          }}
        >
          <FontAwesomeIcon
            style={{ pointerEvents: "none" }}
            icon={faGraduationCap}
          />
          <span style={{ pointerEvents: "none" }}>Student</span>
        </div>
        <div
          className="option"
          onClick={e => {
            let bts = document.querySelectorAll(".toggle-login-as .option")
            for (let b of bts) b.classList.remove("sel")
            e.target.classList.add("sel")
            setAdminEmail("")
            setAdminPassword("")
            setStdEmail("")
            setStdPassword("")
            setLoginAs(1)
          }}
        >
          <FontAwesomeIcon style={{ pointerEvents: "none" }} icon={faUserAlt} />
          <span style={{ pointerEvents: "none" }}>Admin</span>
        </div>
      </div>
    </div>
  )
}

export default Login
