import {
  faSave,
  faTimesCircle,
  faUserEdit,
  faUserPlus,
} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import React, { useEffect, useState } from "react"
import Modal from "../Components/Modal"
import GraphQLFetch from "../Helpers/GraphQLFetch"

function Candidates() {
  const [schools, setSchools] = useState([])
  const [addStudent, setAddStudent] = useState(false)
  const [regno, setRegno] = useState("")
  const [candidates, setCandidates] = useState([])

  useEffect(() => {
    fetchCandidates()
  }, [])

  async function fetchCandidates() {
    await fetchSchools()
  }

  async function fetchSchools() {
    let res = await GraphQLFetch(`{
      schools{
        name
        code
      }
    }`)
    setSchools(res.data.schools)
    setTimeout(() => {
      console.log(schools)
    }, 2000)
  }

  return (
    <div className="students">
      <div className="title">Candidates</div>

      {schools.map((s) => (
        <div className="can-school">
          <div className="school-name">{s.name}</div>
          <div className="can-details">
            <div className="can-chart"></div>
            <div className="can-list"></div>
          </div>
        </div>
      ))}

      <Modal
        title="Add Candidate"
        shown={addStudent}
        hideMe={() => setAddStudent(false)}
      >
        <div className="input">
          <label htmlFor="">Registration Number</label>
          <input
            onChange={(e) => setRegno(e.target.value)}
            style={{ width: 300 }}
            type="text"
          />
        </div>

        <div className="options">
          <div
            className="button black"
            onClick={async () => {
              let { errors, data } = await GraphQLFetch(`mutation{
                addCandidate(reg_no: "${regno}"){
                  reg_no
                  name
                  school{
                    name
                    code
                  }
                }
              }`)
              if (errors) {
                alert(
                  "Failed to add Candidate\n\n" + errors.map((e) => e.message)
                )
              } else {
                alert(`Candidate Added`)
                setAddStudent(false)
              }
              //fetchCandidates()
            }}
          >
            <FontAwesomeIcon icon={faSave} />
            &nbsp; Save
          </div>
        </div>
      </Modal>
    </div>
  )
}

export default Candidates
