import React, { useState, useEffect } from "react"
import GraphQLFetch from "./Helpers/GraphQLFetch"

function Admin() {
  const [candidates, setCandidates] = useState([])
  const [schools, setSchools] = useState([])

  useEffect(() => {
    loadCandidates()
    loadSchools()
  }, [])

  async function loadCandidates() {
    let { data, errors } = await GraphQLFetch(`{
      candidates{
        name
        reg_no
      }
    }`)
    if (data) {
      setCandidates(data.candidates)
    }
  }

  async function loadSchools() {
    let { data, errors } = await GraphQLFetch(`{
      schools{
        name
        code
      }
    }`)
    if (data) {
      setSchools(data.schools)
    }
  }

  async function loadStudents() {
    let { data, errors } = await GraphQLFetch(`{
      students{
        name
        reg_no
      }
    }`)
    if (data) {
      setStudents(data.student)
    }
  }

  return (
    <div className="admin">
      <div className="top-dash">
        <div className="dash-item" title={candidates.map((c) => c.name)}>
          Candidates {candidates.length}
        </div>
        <div className="dash-item">Students</div>
        <div
          className="dash-item"
          title={schools.map((s) => `${s.name} (${s.code})`)}
        >
          Schools {schools.length}
        </div>
      </div>
    </div>
  )
}

export default Admin
