import React, { useEffect, useState } from "react"
import GraphQLFetch from "../Helpers/GraphQLFetch"

function Students() {
  const [students, setStudents] = useState([])

  useEffect(() => {
    fetchStudents()
  }, [])

  async function fetchStudents() {
    let res = await GraphQLFetch(`{
      studets{
        reg_no
        name
        school{
          name
          code
        }
      }
    }`)
    setStudents(res)
    console.log(res)
  }
  return (
    <div className="students">
      <div className="title">
        <div
          exact
          to="/students"
          className="dash-item"
          title={students.map((s) => `${s.name} (${s.reg_no})`)}
        >
          Students
          <div className="count">{students.length}</div>
          <FontAwesomeIcon className="icon" size="3x" icon={faGraduationCap} />
        </div>
      </div>
    </div>
  )
}

export default Students
