import {
  faSave,
  faTimesCircle,
  faUserEdit,
  faUserPlus,
} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import React, { useEffect, useState } from "react"
import Modal from "../Components/Modal"
import GraphQLFetch from "../Helpers/GraphQLFetch"

function Candidates() {
  const [schools, setSchools] = useState([])
  const [addStudent, setAddStudent] = useState(false)
  const [regno, setRegno] = useState("")

  useEffect(() => {
    fetchCandidates()
  }, [])

  async function fetchCandidates() {
    let res = await GraphQLFetch(`{
      candidates{
        reg_no
        name
        school{
          code
          name
        }
        voters{
          name
        }
        voteCount
      }
    }`)
    setSchools(res.data.candidates)
  }
  return (
    <div className="students">
      <div className="title">Candidates</div>
      {schools && (
        <div className="list">
          <div className="options">
            <div className="button" onClick={(e) => setAddStudent(true)}>
              <FontAwesomeIcon icon={faUserPlus} />
              Add
            </div>
          </div>
          <table>
            <tr>
              <th>Reg_No</th>
              <th>Name</th>
              <th>School</th>
              <th>Vote Count</th>
              <th></th>
            </tr>
            {schools.map(({ reg_no, name, school }) => (
              <tr>
                <td>{reg_no.toUpperCase()}</td>
                <td>{name}</td>
                <td>{/* {school.name} ({school.code.toUpperCase()}) */}</td>
                <td style={{ display: "flex", flexDirection: "row" }}>
                  <div className="button small">
                    <FontAwesomeIcon icon={faUserEdit} />
                    Edit
                  </div>
                  <div
                    className="button small"
                    onClick={(e) => {
                      window.confirm("Are you sure?")
                    }}
                  >
                    <FontAwesomeIcon icon={faTimesCircle} />
                    Delete
                  </div>
                </td>
              </tr>
            ))}
          </table>
        </div>
      )}
      <Modal
        title="Add Candidate"
        shown={addStudent}
        hideMe={() => setAddStudent(false)}
      >
        <div className="input">
          <label htmlFor="">Registration Number</label>
          <input
            onChange={(e) => setRegno(e.target.value)}
            style={{ width: 300 }}
            type="text"
          />
        </div>

        <div className="options">
          <div
            className="button black"
            onClick={async () => {
              let { errors, data } = await GraphQLFetch(`mutation{
                addCandidate(reg_no: "${regno}"){
                  reg_no
                  name
                  school{
                    name
                    code
                  }
                }
              }`)
              if (errors) {
                alert(
                  "Failed to add Candidate\n\n" + errors.map((e) => e.message)
                )
              } else {
                alert(`Candidate Added`)
                setAddStudent(false)
              }
              fetchCandidates()
            }}
          >
            <FontAwesomeIcon icon={faSave} />
            &nbsp; Save
          </div>
        </div>
      </Modal>
    </div>
  )
}

export default Candidates
