  const client = useApolloClient()
  const AUTHEDSTD = gql`
  {
    loggedInStudent(token: "${localStorage.getItem("school-token")}"){
      student{
        name
        reg_no
        school{
          name
          code
        }
      }
    }
  }
`

  return auth.loading ? (
    <>Loading...</>
  ) : (
    <>
      <Login reloadAuth={() => {}} />
    </>
  )
}

