import React, { useState, useEffect } from "react"
import GraphQLFetch from "./Helpers/GraphQLFetch"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {
  faGraduationCap,
  faSchool,
  faVoteYea,
} from "@fortawesome/free-solid-svg-icons"

function Admin() {
  const [candidates, setCandidates] = useState([])
  const [schools, setSchools] = useState([])
  const [students, setStudents] = useState([])

  useEffect(() => {
    loadCandidates()
    loadSchools()
    loadStudents()
  }, [])

  async function loadCandidates() {
    let { data, errors } = await GraphQLFetch(`{
      candidates{
        name
        reg_no
      }
    }`)
    if (data) {
      setCandidates(data.candidates)
    }
  }

  async function loadSchools() {
    let { data, errors } = await GraphQLFetch(`{
      schools{
        name
        code
      }
    }`)
    if (data) {
      setSchools(data.schools)
    }
  }

  async function loadStudents() {
    let { data, errors } = await GraphQLFetch(`{
      students{
        name
        reg_no
      }
    }`)
    if (data) {
      setStudents(data.students)
    }
  }

  return (
    <div className="admin">
      <div className="top-dash">
        <div className="dash-item" title={candidates.map((c) => c.name)}>
          Candidates
          <div className="count">{candidates.length}</div>
          <FontAwesomeIcon className="icon" size="5x" icon={faVoteYea} />
        </div>
        <div
          className="dash-item"
          title={students.map((s) => `${s.name} (${s.reg_no})`)}
        >
          Students
          <div className="count">{students.length}</div>
          <FontAwesomeIcon className="icon" size="5x" icon={faGraduationCap} />
        </div>
        <div
          className="dash-item"
          title={schools.map((s) => `${s.name} (${s.code})`)}
        >
          Schools
          <div className="count">{schools.length}</div>
          <FontAwesomeIcon className="icon" size="4x" icon={faSchool} />
        </div>
      </div>
    </div>
  )
}

export default Admin
